import React, { useState } from 'react';
import '../styles/WeatherStyles.css';

const SearchBar = ({ onSearch }) => {
  const [searchTerm, setSearchTerm] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      onSearch(searchTerm.trim());
      setSearchTerm('');
    }
  };

  const handleQuickSearch = (example) => {
    onSearch(example);
  };

  return (
    <div className="search-section">
      <form className="search-bar" onSubmit={handleSubmit}>
        <div className="search-container">
          <input
            type="text"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            placeholder="Search by city, postal code, or coordinates..."
            className="search-input"
          />
          <button type="submit" className="search-button">
            üîç
          </button>
        </div>
      </form>

      <div className="search-examples">
        <p className="examples-title">Try these examples:</p>
        <div className="example-buttons">
          <button 
            className="example-btn" 
            onClick={() => handleQuickSearch('110001,IN')}
          >
            üáÆüá≥ Delhi, India
          </button>
          <button 
            className="example-btn" 
            onClick={() => handleQuickSearch('90210,US')}
          >
            üá∫üá∏ Beverly Hills, USA
          </button>
          <button 
            className="example-btn" 
            onClick={() => handleQuickSearch('SW1A 1AA,GB')}
          >
            üá¨üáß London, UK
          </button>
          <button 
            className="example-btn" 
            onClick={() => handleQuickSearch('M5V 2T6,CA')}
          >
            üá®üá¶ Toronto, Canada
          </button>
          <button 
            className="example-btn" 
            onClick={() => handleQuickSearch('100-0001,JP')}
          >
            üáØüáµ Tokyo, Japan
          </button>
        </div>
        <div className="search-tips">
          <small>
            üí° <strong>Tips:</strong> Use "postalcode,countrycode" for better accuracy (e.g., "110001,IN")
          </small>
        </div>
      </div>
    </div>
  );
};

export default SearchBar;
