import React, { useState, useEffect } from 'react';
import axios from 'axios';
import WeatherCard from './components/WeatherCard';
import SearchBar from './components/SearchBar';
import Forecast from './components/Forecast';
import LoadingSpinner from './components/LoadingSpinner';
import './styles/App.css';

const API_KEY = 'bca4565674a6d492711be356fad77ccd'; // Replace with your API key
const BASE_URL = 'https://api.openweathermap.org/data/2.5';

function App() {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const fetchWeather = async (searchTerm) => {
    setLoading(true);
    setError('');

    try {
      // Determine search type and build URL
      let url;
      if (/^\d+$/.test(searchTerm)) {
        // Zip code
        url = `${BASE_URL}/weather?zip=${searchTerm}&appid=${API_KEY}&units=metric`;
      } else if (/^-?\d+\.?\d*,-?\d+\.?\d*$/.test(searchTerm)) {
        // Coordinates (lat,lon)
        url = `${BASE_URL}/weather?lat=${searchTerm.split(',')[0]}&lon=${searchTerm.split(',')[1]}&appid=${API_KEY}&units=metric`;
      } else {
        // City name
        url = `${BASE_URL}/weather?q=${searchTerm}&appid=${API_KEY}&units=metric`;
      }

      const weatherResponse = await axios.get(url);
      setWeather(weatherResponse.data);

      // Fetch forecast
      const forecastUrl = `${BASE_URL}/forecast?lat=${weatherResponse.data.coord.lat}&lon=${weatherResponse.data.coord.lon}&appid=${API_KEY}&units=metric`;
      const forecastResponse = await axios.get(forecastUrl);
      setForecast(forecastResponse.data);

    } catch (err) {
      setError('Location not found. Please try again!');
      console.error('Error fetching weather:', err);
    } finally {
      setLoading(false);
    }
  };

  // Get user's location on app start
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          fetchWeather(`${latitude},${longitude}`);
        },
        () => {
          // Default to a nice city if location access denied
          fetchWeather('London');
        }
      );
    }
  }, []);

  return (
    <div className="app">
      <div className="container">
        <header className="app-header">
          <h1 className="app-title">üå§Ô∏è Weather </h1>
          <p className="app-subtitle">Your cute weather companion</p>
        </header>

        <SearchBar onSearch={fetchWeather} />

        {loading && <LoadingSpinner />}

        {error && (
          <div className="error-message">
            ‚ùå {error}
          </div>
        )}

        {weather && !loading && (
          <>
            <WeatherCard weather={weather} />
            <Forecast forecast={forecast} />
          </>
        )}
      </div>
    </div>
  );
}

export default App;
