import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import WeatherCard from './components/WeatherCard';
import SearchBar from './components/SearchBar';
import Forecast from './components/Forecast';
import LoadingSpinner from './components/LoadingSpinner';
import './styles/App.css';

const API_KEY = 'YOUR_API_KEY_HERE';
const BASE_URL = 'https://api.openweathermap.org/data/2.5';

function App() {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);

  // Enhanced search type detection that prioritizes city names
  const detectSearchType = useCallback((searchTerm) => {
    // Trim and clean the input
    const cleanTerm = searchTerm.trim();

    // First, check for coordinates (lat,lon) - most specific
    if (/^-?\d+\.?\d*\s*,\s*-?\d+\.?\d*$/.test(cleanTerm)) {
      const [lat, lon] = cleanTerm.split(',').map(coord => coord.trim());
      return { type: 'coordinates', lat, lon };
    }

    // Then check for postal code with country (e.g., "110001,IN")
    if (/^[a-z0-9\s-]+\s*,\s*[a-z]{2}$/i.test(cleanTerm)) {
      const [postalCode, countryCode] = cleanTerm.split(',').map(part => part.trim());
      return { type: 'postalCodeWithCountry', postalCode, countryCode: countryCode.toUpperCase() };
    }

    // Check for pure numbers (postal codes)
    if (/^\d+$/.test(cleanTerm)) {
      return { type: 'postalCode', postalCode: cleanTerm };
    }

    // Check for alphanumeric postal codes (Canada, UK, etc.)
    if (/^[a-z0-9\s-]+$/i.test(cleanTerm) && cleanTerm.length <= 10 && !/\s{2,}/.test(cleanTerm)) {
      // Additional check to avoid mistaking short city names as postal codes
      const mightBePostalCode = /^[a-z][0-9][a-z]\s*[0-9][a-z][0-9]$/i.test(cleanTerm) || // Canadian format
                               /^[a-z]{1,2}\d{1,2}\s*\d{1,2}[a-z]{2}$/i.test(cleanTerm); // UK format

      if (mightBePostalCode) {
        return { type: 'postalCode', postalCode: cleanTerm };
      }
    }

    // DEFAULT: Everything else is treated as city name
    return { type: 'city', cityName: cleanTerm };
  }, []);

  const fetchWeather = useCallback(async (searchTerm) => {
    if (!searchTerm.trim()) {
      setError('Please enter a location to search');
      return;
    }

    setLoading(true);
    setError('');

    try {
      const searchType = detectSearchType(searchTerm);
      let url;

      console.log('Search type detected:', searchType);

      switch (searchType.type) {
        case 'coordinates':
          url = `${BASE_URL}/weather?lat=${searchType.lat}&lon=${searchType.lon}&appid=${API_KEY}&units=metric`;
          break;

        case 'postalCodeWithCountry':
          url = `${BASE_URL}/weather?zip=${searchType.postalCode},${searchType.countryCode}&appid=${API_KEY}&units=metric`;
          break;

        case 'postalCode':
          url = `${BASE_URL}/weather?zip=${searchType.postalCode}&appid=${API_KEY}&units=metric`;
          break;

        case 'city':
        default:
          url = `${BASE_URL}/weather?q=${encodeURIComponent(searchType.cityName)}&appid=${API_KEY}&units=metric`;
          break;
      }

      console.log('Fetching from:', url);
      const weatherResponse = await axios.get(url);

      // Add to search history
      setSearchHistory(prev => [
        { 
          name: weatherResponse.data.name, 
          country: weatherResponse.data.sys.country,
          searchTerm: searchTerm,
          timestamp: new Date().toLocaleString() 
        },
        ...prev.slice(0, 4)
      ]);

      setWeather(weatherResponse.data);

      // Fetch forecast
      const forecastUrl = `${BASE_URL}/forecast?lat=${weatherResponse.data.coord.lat}&lon=${weatherResponse.data.coord.lon}&appid=${API_KEY}&units=metric`;
      const forecastResponse = await axios.get(forecastUrl);
      setForecast(forecastResponse.data);

    } catch (err) {
      console.error('Error fetching weather:', err.response?.data || err.message);

      if (err.response?.data?.cod === '404') {
        setError(`"${searchTerm}" not found. Try "City, Country" format like "London, UK" or "Tokyo, JP"`);
      } else if (err.response?.data?.message) {
        setError(`Error: ${err.response.data.message}`);
      } else if (err.code === 'NETWORK_ERROR') {
        setError('Network error. Please check your internet connection.');
      } else {
        setError('Failed to fetch weather data. Please try again.');
      }
    } finally {
      setLoading(false);
    }
  }, [API_KEY, BASE_URL, detectSearchType]);

  // Get user's location on app start
  useEffect(() => {
    const initializeApp = async () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            const { latitude, longitude } = position.coords;
            fetchWeather(`${latitude},${longitude}`);
          },
          (error) => {
            console.log('Geolocation error:', error);
            // Default to a major city if location access denied
            fetchWeather('London');
          },
          {
            timeout: 10000,
            enableHighAccuracy: false
          }
        );
      } else {
        // Fallback if geolocation is not supported
        fetchWeather('London');
      }
    };

    initializeApp();
  }, [fetchWeather]);

  return (
    <div className="app">
      <div className="container">
        <header className="app-header">
          <h1 className="app-title">üå§Ô∏è Global Weather</h1>
          <p className="app-subtitle">Your worldwide weather companion</p>
        </header>

        <SearchBar onSearch={fetchWeather} />

        {loading && <LoadingSpinner />}

        {error && (
          <div className="error-message">
            ‚ùå {error}
          </div>
        )}

        {weather && !loading && (
          <>
            <WeatherCard weather={weather} />
            <Forecast forecast={forecast} />
          </>
        )}

        {searchHistory.length > 0 && (
          <div className="search-history">
            <h3>Recent Searches</h3>
            <div className="history-list">
              {searchHistory.map((item, index) => (
                <div 
                  key={index} 
                  className="history-item"
                  onClick={() => fetchWeather(item.searchTerm)}
                >
                  <span className="history-name">{item.name}, {item.country}</span>
                  <span className="history-term">(Searched: {item.searchTerm})</span>
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
