import React, { useState, useEffect } from 'react';
import axios from 'axios';
import WeatherCard from './components/WeatherCard';
import SearchBar from './components/SearchBar';
import Forecast from './components/Forecast';
import LoadingSpinner from './components/LoadingSpinner';
import './styles/App.css';

const API_KEY = 'bca4565674a6d492711be356fad77ccd';
const BASE_URL = 'https://api.openweathermap.org/data/2.5';

function App() {
  const [weather, setWeather] = useState(null);
  const [forecast, setForecast] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [searchHistory, setSearchHistory] = useState([]);

  // Enhanced postal code detection with country support
  const detectSearchType = (searchTerm) => {
    // Trim and clean the input
    const cleanTerm = searchTerm.trim().toLowerCase();

    // Coordinates (lat,lon)
    if (/^-?\d+\.?\d*,\s*-?\d+\.?\d*$/.test(cleanTerm)) {
      const [lat, lon] = cleanTerm.split(',').map(coord => coord.trim());
      return { type: 'coordinates', lat, lon };
    }

    // Postal code with country (e.g., "110001,IN" or "90210,US")
    if (/^[a-z0-9\s-]+,[a-z]{2}$/i.test(cleanTerm)) {
      const [postalCode, countryCode] = cleanTerm.split(',').map(part => part.trim());
      return { type: 'postalCodeWithCountry', postalCode, countryCode: countryCode.toUpperCase() };
    }

    // Pure numbers (assume postal code)
    if (/^\d+$/.test(cleanTerm)) {
      return { type: 'postalCode', postalCode: cleanTerm };
    }

    // Alphanumeric postal codes (common in Canada, UK, etc.)
    if (/^[a-z0-9\s-]+$/i.test(cleanTerm) && cleanTerm.length <= 10) {
      return { type: 'postalCode', postalCode: cleanTerm };
    }

    // Default to city name
    return { type: 'city', cityName: cleanTerm };
  };

  const fetchWeather = async (searchTerm) => {
    setLoading(true);
    setError('');

    try {
      const searchType = detectSearchType(searchTerm);
      let url;

      switch (searchType.type) {
        case 'coordinates':
          url = `${BASE_URL}/weather?lat=${searchType.lat}&lon=${searchType.lon}&appid=${API_KEY}&units=metric`;
          break;

        case 'postalCodeWithCountry':
          url = `${BASE_URL}/weather?zip=${searchType.postalCode},${searchType.countryCode}&appid=${API_KEY}&units=metric`;
          break;

        case 'postalCode':
          // Try without country code first, OpenWeatherMap will try to guess
          url = `${BASE_URL}/weather?zip=${searchType.postalCode}&appid=${API_KEY}&units=metric`;
          break;

        case 'city':
        default:
          url = `${BASE_URL}/weather?q=${searchType.cityName}&appid=${API_KEY}&units=metric`;
          break;
      }

      console.log('Fetching from:', url);
      const weatherResponse = await axios.get(url);

      // Add to search history
      setSearchHistory(prev => [
        { 
          name: weatherResponse.data.name, 
          country: weatherResponse.data.sys.country,
          timestamp: new Date().toLocaleString() 
        },
        ...prev.slice(0, 4) // Keep only last 5 searches
      ]);

      setWeather(weatherResponse.data);

      // Fetch forecast
      const forecastUrl = `${BASE_URL}/forecast?lat=${weatherResponse.data.coord.lat}&lon=${weatherResponse.data.coord.lon}&appid=${API_KEY}&units=metric`;
      const forecastResponse = await axios.get(forecastUrl);
      setForecast(forecastResponse.data);

    } catch (err) {
      console.error('Error details:', err.response?.data);

      if (err.response?.data?.cod === '404') {
        setError('Location not found. Please check your search and try again!');
      } else if (err.response?.data?.message) {
        setError(`Error: ${err.response.data.message}`);
      } else {
        setError('Failed to fetch weather data. Please check your internet connection and try again.');
      }
    } finally {
      setLoading(false);
    }
  };

  // Get user's location on app start
  useEffect(() => {
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const { latitude, longitude } = position.coords;
          fetchWeather(`${latitude},${longitude}`);
        },
        () => {
          // Default to a major city if location access denied
          fetchWeather('New Delhi,IN');
        }
      );
    }
  }, []);

  return (
    <div className="app">
      <div className="container">
        <header className="app-header">
          <h1 className="app-title">üå§Ô∏è Global Weather</h1>
          <p className="app-subtitle">Your worldwide weather companion</p>
        </header>

        <SearchBar onSearch={fetchWeather} />

        {loading && <LoadingSpinner />}

        {error && (
          <div className="error-message">
            ‚ùå {error}
          </div>
        )}

        {weather && !loading && (
          <>
            <WeatherCard weather={weather} />
            <Forecast forecast={forecast} />
          </>
        )}

        {searchHistory.length > 0 && (
          <div className="search-history">
            <h3>Recent Searches</h3>
            <div className="history-list">
              {searchHistory.map((item, index) => (
                <div key={index} className="history-item">
                  {item.name}, {item.country}
                </div>
              ))}
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
